	public Poem getPoemByID(String poemid) {
		Poem poemByID = null;

		for (int i = 0; i < listOfPoems.size(); i++) {
			Poem poem = listOfPoems.get(i);
			if (poem != null && poem.getPoemID() != null && poem.getPoemID().equals(poemid)) {
				poemByID = poem;
				break;
			}
		}
		return poemByID;
	}
